package main

import (
	"flag"
	"fmt"
	"html/template"
	"log"
	"net/http"
	"strings"

	"github.com/gorilla/mux"
	pb "github.com/tektoncd/results/proto/v1alpha2/results_go_proto"
	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
)

var (
	// Command line flags
	// Cert file can be generated by: kubectl get secrets tekton-results-tls -n tekton-pipelines --template='{{index .data "tls.crt"}}' | base64 -d > /tmp/results.crt
	certFile = flag.String("cert", "/tmp/results.crt", "Path to the TLS certificate file.")
	target   = flag.String("target", "tekton-results-api-service.tekton-pipelines.svc.cluster.local", "Target server name")
	port     = flag.String("port", "8081", "Port to listen on")
	grpcAddr = flag.String("grpc-addr", "localhost:8080", "gRPC server address")

	// Templates
	resultTmpl = template.Must(template.New("results.html").ParseFiles("templates/results.html"))
	recordTmpl = template.Must(template.New("records.html").
			Funcs(template.FuncMap{
			"textproto":  textproto,
			"parent":     parent,
			"trimprefix": strings.TrimPrefix,
		}).
		ParseFiles("templates/records.html"))
)

func (u *ui) home(w http.ResponseWriter, r *http.Request) {
	fmt.Fprint(w, "Add namespace to URL (/<namespace>) ^^^")
}

func (u *ui) results(w http.ResponseWriter, r *http.Request) {
	res, err := u.client.ListResults(r.Context(), &pb.ListResultsRequest{
		Parent: strings.Trim(r.URL.Path, "/"),
	})
	if err != nil {
		w.WriteHeader(http.StatusInternalServerError)
		fmt.Fprintln(w, err)
		return
	}
	if err := resultTmpl.Execute(w, res); err != nil {
		return
	}
}

func (u *ui) records(w http.ResponseWriter, r *http.Request) {
	req := &pb.ListRecordsRequest{
		Parent: strings.Trim(r.URL.Path, "/"),
		Filter: r.FormValue("query"),
	}
	res, err := u.client.ListRecords(r.Context(), req)
	if err != nil {
		w.WriteHeader(http.StatusInternalServerError)
		fmt.Fprintln(w, err)
		return
	}
	if err := recordTmpl.Execute(w, struct {
		Request  *pb.ListRecordsRequest
		Response *pb.ListRecordsResponse
	}{
		Request:  req,
		Response: res,
	}); err != nil {
		return
	}
}

type ui struct {
	client pb.ResultsClient
}

func main() {
	// Parse command line flags
	flag.Parse()

	// Load the TLS certificate
	creds, err := credentials.NewClientTLSFromFile(*certFile, *target)
	if err != nil {
		log.Fatalf("failed to load credentials: %v", err)
	}

	// Connect to the gRPC server
	conn, err := grpc.NewClient(*grpcAddr,
		grpc.WithTransportCredentials(creds),
		grpc.WithAuthority(*target))
	if err != nil {
		log.Fatalf("did not connect: %v", err)
	}
	defer conn.Close()
	u := &ui{
		client: pb.NewResultsClient(conn),
	}
	log.Printf("Connected to gRPC server at %s", *grpcAddr)

	r := mux.NewRouter()
	r.HandleFunc("/", u.home)
	r.HandleFunc("/{namespace}", u.results)
	r.HandleFunc("/{namespace}/results/{name}", u.records)
	http.Handle("/", r)

	addr := ":" + *port
	log.Printf("Running on http://localhost%s", addr)
	log.Fatal(http.ListenAndServe(addr, nil))
}
